changeSet.sql:
-- Insert 100,000 regular customers INSERT INTO customers (name, email, phone, customer_type, created_at) SELECT 'Customer ' || i, 'customer' || i || '@example.com', '+1-555-' || LPAD(i::text, 7, '0'), 'REGULAR', NOW() - (random() * INTERVAL '365 days') FROM generate_series(1, 100000) AS i;
-- For each customer, create 1-3 buildings INSERT INTO buildings (name, address, building_type, square_meters, customer_id) SELECT 'Building ' || (ROW_NUMBER() OVER (PARTITION BY c.id)) || ' of Customer ' || c.id, (1000 + c.id) || ' Main Street, City, State', CASE (random() * 2)::int WHEN 0 THEN 'RESIDENTIAL' WHEN 1 THEN 'COMMERCIAL' ELSE 'INDUSTRIAL' END, (50 + (random() * 450))::int, c.id FROM customers c CROSS JOIN generate_series(1, (1 + (random() * 2)::int)) AS building_num WHERE c.customer_type = 'REGULAR';
-- For each building, create 1-10 devices INSERT INTO devices (name, device_type, manufacturer, model_number, power_consumption, installation_date, status, building_id) SELECT 'Device ' || (ROW_NUMBER() OVER (PARTITION BY b.id)), CASE (random() * 5)::int WHEN 0 THEN 'SENSOR' WHEN 1 THEN 'ACTUATOR' WHEN 2 THEN 'CONTROLLER' WHEN 3 THEN 'CAMERA' WHEN 4 THEN 'THERMOSTAT' ELSE 'SMART_LOCK' END, CASE (random() * 3)::int WHEN 0 THEN 'TechCorp' WHEN 1 THEN 'SmartDevices Inc' WHEN 2 THEN 'IoT Solutions' ELSE 'AutomationPro' END, 'MODEL-' || LPAD(((random() * 9999)::int)::text, 4, '0'), (5 + (random() * 95))::int, NOW() - (random() * INTERVAL '730 days'), CASE (random() * 10)::int WHEN 0 THEN 'MAINTENANCE' WHEN 1 THEN 'INACTIVE' ELSE 'ACTIVE' END, b.id FROM buildings b CROSS JOIN generate_series(1, (1 + (random() * 9)::int)) AS device_num WHERE b.customer_id IN (SELECT id FROM customers WHERE customer_type = 'REGULAR');
